template:
var numTraces = 4;
var traySelect = [];
var nameSelect = [];
var propSelect=[];
var attrSelect=[];
var valSelect=[];
var trayInfo = [];
var permitGetTrayValues = true;


useWebSockets(true);
function onDocumentReady()
{
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        traySelect[itrace] = document.getElementById('traySelect' + itrace.toString());
        nameSelect[itrace] = document.getElementById('nameSelect' + itrace.toString());
        propSelect[itrace] = document.getElementById('propSelect' + itrace.toString());
        attrSelect[itrace] = document.getElementById('attrSelect' + itrace.toString());
        valSelect[itrace] = document.getElementById('valSelect' + itrace.toString());
        trayInfo[itrace] = {type: '', name : '',  prop : '',attr : '',tray : {}, axis:'y1', pts:true};
        $('#unitSelect'+ itrace.toString()).html('');
    }
    setInterval(getTrayValues, 1000);
    onDocumentReadySpecial();
}
function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getTrayList':
                loadTrayList(msg.payload);
                break;
            case 'getNameList':
                loadNameList(msg);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getTrayValues':
                loadTrayValues(msg);
                break;
            case 'permissionError':
                acknowledgeDialog('Permission', 'Error', msg.payload);
                break;
            case 'loginExpired':
                location.reload();
                break;
            default:
                onWebSocketMessageSpecial(msg);
        }
    }
 }
function onWebSocketOpen()
{
    var actionMsg = 
        {
        };
    sendActionMsg('getTrayList', 'readDatabase', actionMsg);
}
function onWebSocketClose()
{

}
function loadTrayList(trayList)
{
    trayList.sort();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        for (var ii = 0; ii < trayList.length; ++ii)
        {
            var opt = document.createElement('option');
            opt.value = trayList[ii];
            opt.innerHTML = trayList[ii];
            traySelect[itrace].appendChild(opt);
        }
    }
}
function loadNameList(data)
{
    data.nameList.sort();
    for (var ii = 0; ii < data.nameList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.nameList[ii];
        opt.innerHTML = data.nameList[ii];
        nameSelect[data.trace].appendChild(opt);
    }

    return;
}
function loadAttrList(data)
{
    data.attrList.sort();
    for (var ii = 0; ii < data.attrList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.attrList[ii];
        opt.innerHTML = data.attrList[ii];
        attrSelect[data.trace].appendChild(opt);
    }
}
function traySelected(itrace)
{
    clearNameOptionList(itrace);
    if (traySelect[itrace].value == 'notSelected') return;
    nameSelect[itrace].value = 'notSelected';
    var actionMsg = 
        {
            trace       : itrace,
            type  : traySelect[itrace].value
        };
    sendActionMsg('getNameList', 'readDatabase', actionMsg);
}
function nameSelected(itrace)
{
    clearPropOptionList(itrace);
    if (nameSelect[itrace].value == 'notSelected') return;
    var readOpt = document.createElement('option');
    readOpt.value = 'reading';
    readOpt.innerHTML = 'reading';
    propSelect[itrace].appendChild(readOpt);
    var setOpt = document.createElement('option');
    setOpt.value = 'setting';
    setOpt.innerHTML = 'setting';
    propSelect[itrace].appendChild(setOpt);

    propSelect[itrace].value = 'notSelected';
}
function propSelected(itrace)
{
    clearAttrOptionList(itrace);
    if (propSelect[itrace].value == 'notSelected') return;
    var actionMsg = 
        {
            trace       : itrace,
            type  : traySelect[itrace].value,
            name        : nameSelect[itrace].value,
            prop        : propSelect[itrace].value
        };
    sendActionMsg('getAttrList', 'readDatabase', actionMsg);
}
function attrSelected(itrace)
{
    trayInfo[itrace].type = '';
    trayInfo[itrace].name = '';
    trayInfo[itrace].prop = '';
    trayInfo[itrace].attr = '';
    trayInfo[itrace].tray = {};
    if (attrSelect[itrace].value == 'notSelected') 
    {
        $('#valueSelect' + itrace.toString()).html('');
        $('#unitSelect' + itrace.toString()).html('');
        $('#editTrayButton'+ itrace.toString()).hide();

        return;
    }
    trayInfo[itrace].type = traySelect[itrace].value;
    trayInfo[itrace].name = nameSelect[itrace].value;
    trayInfo[itrace].prop = propSelect[itrace].value;
    trayInfo[itrace].attr = attrSelect[itrace].value;
    attrSelectedSpecial(itrace);

}
function clearNameOptionList(itrace)
{
    trayInfo[itrace].type = '';
    trayInfo[itrace].name = '';
    trayInfo[itrace].prop = '';
    trayInfo[itrace].attr = '';
    trayInfo[itrace].tray = {};

    while (nameSelect[itrace].firstChild)
    {
        nameSelect[itrace].removeChild(nameSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    nameSelect[itrace].appendChild(opt);
    clearPropOptionList(itrace);
}
function clearPropOptionList(itrace)
{
    trayInfo[itrace].type = '';
    trayInfo[itrace].name = '';
    trayInfo[itrace].prop = '';
    trayInfo[itrace].attr = '';
    trayInfo[itrace].tray = {};

    while (propSelect[itrace].firstChild)
    {
        propSelect[itrace].removeChild(propSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    propSelect[itrace].appendChild(opt);
    clearAttrOptionList(itrace);
}
function clearAttrOptionList(itrace)
{
    trayInfo[itrace].type = '';
    trayInfo[itrace].name = '';
    trayInfo[itrace].prop = '';
    trayInfo[itrace].attr = '';
    trayInfo[itrace].tray = {};

    while (attrSelect[itrace].firstChild)
    {
        attrSelect[itrace].removeChild(attrSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    attrSelect[itrace].appendChild(opt);
    $('#unitSelect'+ itrace.toString()).html('');
    $('#valueSelect' + itrace.toString()).html('');
    clearAttrOptionListSpecial(itrace);
}
function getTrayValues()
{
    var trayList = [];
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if ((trayInfo[itrace].type.length > 0) && (trayInfo[itrace].name.length > 0)) 
        {
            trayList[traceCount] = { $and: [ {type : trayInfo[itrace].type}, {name : trayInfo[itrace].name}]};
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    var queryFilter = { $or: trayList };
    var actionMsg = 
        {
            queryFilter : queryFilter
        };
    sendActionMsg('getTrayValues', 'readDatabase', actionMsg);
}
function loadTrayValues(data)
{
    if (!permitGetTrayValues) return;
    var trays = data.trays;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (trayInfo[itrace].attr.length > 0)
        {
            for (itray = 0; itray < trays.length; ++itray)
            {
                if (trays[itray].type == trayInfo[itrace].type)
                {
                    if(trays[itray].name == trayInfo[itrace].name)
                    {
                        trayInfo[itrace].tray = trays[itray];
                        var attr = trayInfo[itrace].tray[trayInfo[itrace].attr];
                        var valueColor = 'black';
                        if (attr.alarm.value > 0) valueColor = 'orange';
                        if (attr.alarm.value > 2) valueColor = 'red';
                        $('#valueSelect' + itrace.toString()).css("color", valueColor);
                        $('#valueSelect' + itrace.toString()).html(attr.value.toString());
                        $('#unitSelect' + itrace.toString()).css("color", valueColor);
                        $('#unitSelect' + itrace.toString()).html(attr.unit);
                    }
                }
            } 
        }
    }
    loadTrayValuesSpecial(data);
}

func:
var boxCookie = msg.req.cookies[global.get('box') + 'Role'];
if(boxCookie == undefined)
{
    msg.cookies = { };
    msg.payload.errorMsg = "Credentials needed";
    return [null,msg];
}
msg.token = boxCookie;
msg.payload['roleToken'] = msg.token;
return [msg,null];

var tokenError = msg.error;
if (tokenError != undefined)
{
    msg.payload.errorMsg = tokenError;
    msg.cookies = { };
    return [null,msg];
}
if (msg.token.expDate > 0)
{
    if (msg.token.expDate < new Date().getTime())
    {
        msg.payload.errorMsg = 'Login Expired. Please login again';
        msg.cookies = { };
        return [null,msg];
    }
}
var permittedRole = false;
for (let irole = 0; irole < msg.payload.allowedRoles.length; ++irole)
{
    for (let itoken = 0; itoken < msg.token.roles.length; ++itoken)
    {
        if (msg.token.roles[itoken] == msg.payload.allowedRoles[irole]) permittedRole = true;
    }
}
if (!permittedRole)
{
    msg.payload.errorMsg = 'Credentials not valid for App';
    msg.cookies = { };
    return [null,msg];
}
msg.payload['profile'] = JSON.stringify(msg.token);
return [msg,null];

