let ireq = -1;
for (let ii = 0; ii < msg.req.rawHeaders.length; ++ii)
{
    if (msg.req.rawHeaders[ii] == "X-Forwarded-Proto") ireq = ii + 1;
}
if (ireq >= 0) 
{
    if (msg.req.rawHeaders[ireq] == 'http')
    {
        msg.cookies = { };
        let ireq2 = -1;
        for (let ii = 0; ii < msg.req.rawHeaders.length; ++ii)
        {
            if (msg.req.rawHeaders[ii] == "Host") ireq2 = ii + 1;
        }
        if (ireq2 >= 0)
        {
            let host = msg.req.rawHeaders[ireq2];
            let url = "https://" + host + msg.req.url;
            msg.payload['httpsUrl'] = url;
        }
        else
        {
            msg.payload['httpsUrl'] = '';
        }
        msg.payload.errorMsg = "Insecure Connection!";
        return [null,msg];
    }
}
return [msg,null];

let app = msg.req['_parsedUrl'].pathname.split('/')[1];
global.set(app, msg.payload.trayUpdateFilter);
