//onOffSet
        class OnOffSetCube extends CubeRowParameterView
        {
            addComponents(widgetColumns)
            {

                this.settingButton = document.createElement('button');
                this.settingButton.setAttribute('class', 'btn btn-block onOffSetCube-button');
                this.settingButton.innerHTML = '';
                this.settingButton.onclick = event => {this.toggle()};
                widgetColumns[0].appendChild(this.settingButton);
            }
            toggle()
            {
                let onOff = 0;
                if (this.tray[this.config.cubeName].value === 0)
                {
                    onOff = 1;
                }
                else
                {
                    onOff = 0;
                }
                this.settingButton.setAttribute('style','background: var(--color3); color: var(--color3);');
                let payload = {'cube': this.config.cubeName, 'value':onOff};
                let actionMsg = 
                    {
                        topic   : this.card.app.box + '/' +  this.tray.type + '/' +  this.tray.name + '/setting/setting',
                        payload : payload
                    };
                this.card.app.sendActionMsg('setting','setting',actionMsg);
            }
            updateCubeValue()
            {
                if (this.tray[this.config.cubeName].value === 0)
                {
                    if ('onOffLabel' in this.config)
                    {
                        this.settingButton.innerHTML = this.config.onOffLabel[0];
                    }
                    else
                    {
                        this.settingButton.innerHTML = "Off";
                    }

                    this.settingButton.setAttribute('style','background:' + this.config.offColor + '; color: black;');
                }
                if (this.tray[this.config.cubeName].value === 1) 
                {   
                    if ('onOffLabel' in this.config)
                    {
                        this.settingButton.innerHTML = this.config.onOffLabel[1];
                    }
                    else
                    {
                        this.settingButton.innerHTML = "On";
                    }
                    this.settingButton.setAttribute('style','background:' + this.config.onColor + '; color: black;');
                }
            }
        }

