//cubeRowParameterView
        class CubeRowParameterView 
        {
            constructor(card, config)
            {
                this.card = card;
                this.config = JSON.parse(JSON.stringify(config));
            }

            onDocumentReady(parentElement)
            {
                let inLineTable = document.createElement('table');
                inLineTable.setAttribute('width', '100%');
                let inLineRow  = document.createElement('tr');
                let labelColumn  = document.createElement('td');
                labelColumn.setAttribute('class', 'cubeRowParameterView-label-column');

                let labelSpan  = document.createElement('span');
                if ('cubeLabel' in this.config)
                {
                    labelSpan.innerHTML = this.config.cubeLabel;
                }
                else
                {
                    labelSpan.innerHTML = this.card.app.trays[this.config.trayIndex].type + "." + this.card.app.trays[this.config.trayIndex].name + "." + this.config.cubeName;
                }
                labelColumn.appendChild(labelSpan);
                inLineRow.appendChild(labelColumn);

                let widgetColumns = [];
                for (let iw = 0; iw < 3; ++iw)
                {
                    let iwcol  = document.createElement('td');
                    iwcol.setAttribute('class', 'cubeRowParameterView-widget-column-' + (iw + 1).toString());
                    inLineRow.appendChild(iwcol);
                    widgetColumns.push(iwcol);
                }

                this.addComponents(widgetColumns);

                let alarmColumn  = document.createElement('td');
                alarmColumn.setAttribute('class', 'cubeRowParameterView-alarmConfig-column');
                this.alarmButton = document.createElement('input');
                this.alarmButton.setAttribute('type', 'image');
                this.alarmButton.setAttribute('src', '/img/greenLight.png');
                this.alarmButton.setAttribute('class', 'cubeRowParameterView-alarmConfig-button');
                this.alarmButton.onclick = event => {this.changeAlarmConfig()};
                alarmColumn.appendChild(this.alarmButton);
                inLineRow.appendChild(alarmColumn);

                inLineTable.appendChild(inLineRow);
                parentElement.appendChild(inLineTable);
            }

            trayUpdate(trays)
            {
                this.tray = trays[this.config.trayIndex];
                this.updateCubeValue();
                switch(this.tray[this.config.cubeName].alarm.value)
                {
                    case 0:
                        this.alarmButton.src="/img/greenLight.png";
                        break;
                    case 1:
                        this.alarmButton.src="/img/blueLight.png";
                        break;
                    case 2:
                        this.alarmButton.src="/img/yellowLight.png";
                        break;
                    case 3:
                        this.alarmButton.src="/img/purpleLight.png";
                        break;
                    case 4:
                        this.alarmButton.src="/img/redLight.png";
                        break;
                    default:
                        this.alarmButton.src="/img/greyLight.png";
                        break;

                }
            }
            changeAlarmConfig()
            {
                this.card.app.alarmLimitDialog.editAlarmConfig(this.config.cubeName, this.tray);
            }

        }

//cubeRowCard
        class CubeRowCard 
        {
            constructor(app, config)
            {
                this.app = app;
                this.config = config;
                this.cubeRows = [];
                for (let icube = 0; icube < this.config.cubeRows.length; ++icube)
                {
                    let cubeRow = new (dict.get(this.config.cubeRows[icube].type))(this, this.config.cubeRows[icube].config);
                    this.cubeRows.push(cubeRow);
                }
            }
            onDocumentReady()
            {
                let containerRowDiv = document.createElement('div');
                containerRowDiv.setAttribute('class', 'row cubeRowCard-containerRow');
                document.getElementById('appContent').appendChild(containerRowDiv);

                let containerColDiv = document.createElement('div');
                containerColDiv.setAttribute('class', 'col-md-12');
                containerColDiv.setAttribute('align', 'center');
                containerRowDiv.appendChild(containerColDiv);

                let cardDiv = document.createElement('div');
                cardDiv.setAttribute('class', 'card cubeRowCard');
                containerColDiv.appendChild(cardDiv);

                let titleTable = document.createElement('table');
                titleTable.setAttribute('width', '100%');
                let titleRow  = document.createElement('tr');
                let titleCol  = document.createElement('td');
                titleCol.setAttribute('width', '85%');
                titleCol.setAttribute('align', 'left');
                let titleSpan  = document.createElement('span');
                titleSpan.setAttribute('class', 'cubeRowCard-title');
                titleSpan.innerHTML = this.config.title;
                titleCol.appendChild(titleSpan);
                titleRow.appendChild(titleCol);
                let buttonCol  = document.createElement('td');
                buttonCol.setAttribute('width', '15%');

                this.displayButton = document.createElement('button');
                this.displayButton.setAttribute('class', 'btn btn-block cubeRowCard-title-button');
                this.displayButton.innerHTML = '-';
                this.displayButton.onclick = event => {this.toggle()};
                buttonCol.appendChild(this.displayButton);
                titleRow.appendChild(buttonCol);

                this.cardBodyDiv = document.createElement('div');
                this.cardBodyDiv.setAttribute('class', 'cubeRowCard-body');
                this.cardBodyDiv.setAttribute('align', 'center');
                let cardBodyTable = document.createElement('table');
                cardBodyTable.setAttribute('width', '100%');

                for (let ic = 0; ic < this.cubeRows.length; ++ic)
                {
                    let cubeRowRow =  document.createElement('tr');
                    let cubeRowCol  = document.createElement('td');
                    cubeRowCol.setAttribute('width', '100%');
                    this.cubeRows[ic].onDocumentReady(cubeRowCol);
                    cubeRowRow.appendChild(cubeRowCol);
                    cardBodyTable.appendChild(cubeRowRow);
                }
                this.cardBodyDiv.appendChild(cardBodyTable);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);
                if (this.config.displayOnOpen)
