            buttonClicked(button)
            {
                if (this.buttonPressed >= 0)
                {
                    this.archiveButtonList[this.buttonPressed].classList.remove('archive-button-pressed');
                    this.archiveButtonList[this.buttonPressed].classList.add('archive-button');
                }
                this.buttonPressed = button;

                for (let ib = 0; ib < 6; ++ib)  this.archiveButtonList[ib].disabled = true;
                this.archiveButtonList[button].classList.remove('archive-button');
                this.archiveButtonList[button].classList.add('archive-button-pressed');

                this.archiveRequestList = [];
                this.archiveList = [];
                let stopDate = new Date().getTime();
                let startDate = this.config.buttonNumbers[button] * 3600000;
                if (this.config.timeUnit == 'day') startDate = 24 * startDate; 
                startDate = stopDate - startDate;
                this.archiveRequestList = [];
                this.archiveList = [];

                for (let itray = 0; itray < this.app.trays.length; ++itray)
                {
                    let cubeNameList = [];
                    for (let icube = 0; icube < this.config.cubeList[itray].length; ++icube) 
                        cubeNameList.push(this.config.cubeList[itray][icube].name);
                    this.archiveRequestList[itray] = 
                    {
                        type : this.app.trays[itray].type,
                        name : this.app.trays[itray].name, 
//need to test what happens when cubeList is empty
                        cube : cubeNameList,
                        startDate: startDate, 
                        stopDate: stopDate,
                    };

                }
                this.getArchiveValues();
            }
            getArchiveValues()
            {
                let actionMsg = 
                {
                    tray      : this.archiveRequestList[0]
                };
                this.app.sendActionMsg('readDatabase','readDatabase', actionMsg);
                this.archiveRequestList.shift();
                return;
            }
            putArchiveValues(data)
            {
                this.archiveList.push(data);
                if (this.archiveRequestList.length > 0)
                {
                    this.getArchiveValues();
                }
                else
                {
                    this.plotArchive();
                }
            }
            toggle()
            {

                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                    if (this.archivePlotTraces.length > 0)
                    {
                        this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                        Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                    }
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.archivePlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            plotArchive()
            {
                this.archivePlotTraces = [];
                for (let itray = 0; itray < this.archiveList.length; ++itray)
                {
                    if (this.archiveList[itray].trace != null)
                    {
                        for (let icube = 0; icube < this.archiveList[itray].tray.cube.length; ++icube)
                        {
                            let traceType = 'scatter';
                            if ("type" in this.config.cubeList[itray][icube]) 
                            {
                                traceType = this.config.cubeList[itray][icube].type;
                            }
                            let lineMode = 'lines';
                            let markerSize = 12;
                            if ("mode" in this.config.cubeList[itray][icube]) lineMode = this.config.cubeList[itray][icube].mode;
                            if ("size" in this.config.cubeList[itray][icube]) markerSize = this.config.cubeList[itray][icube].size;
                            let trace =
                            {
                                x: [],
                                y: [],
                                name: this.archiveList[itray].tray.type + '.' + this.archiveList[itray].tray.name + '.' + this.archiveList[itray].tray.cube[icube],
                                yaxis: this.config.cubeList[itray][icube].yaxis,
                                type: traceType,
                                mode: lineMode,
                                marker: { size: markerSize },
                                line: { color: this.config.cubeList[itray][icube].color }
                            };
                            for (let ipt = 0; ipt < this.archiveList[itray].trace.length; ++ipt)
                            {
                                trace.x.push(this.archiveList[itray].trace[ipt].timeStamp);
                                trace.y.push(this.archiveList[itray].trace[ipt][this.archiveList[itray].tray.cube[icube]]);
                            }
                            this.archivePlotTraces.push(trace);
                        }
                    }
                }
                if (this.archivePlotTraces.length > 0)
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('archiveChart', this.archivePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
                for (let ib = 0; ib < 6; ++ib) 
                {
                    this.archiveButtonList[ib].disabled = false;
                }
            }

        }

