// From http://www.featureblend.com/css-json.html
function jsonToCss(cssjson)
{
    var styleStr = "\t\t";
    for(var i in cssjson)
    {
        styleStr += i + "\n\t\t{\n"
        for(var j in cssjson[i]){
            styleStr += "\t\t\t" + j + "\t\t:\t" + cssjson[i][j] + ";\n"     
        }
        styleStr += "\t\t}\n"  
    }
    return styleStr;
}
