// alarmLimitDialog
        class AlarmLimitDialog
        {
            constructor(app)
            {
                this.app = app;
            }
            onDocumentReady(cardContainerId)
            {
                this.cardContainer = document.getElementById(cardContainerId);

                let limitName = ["LOLO", "LOW", "HIGH", "HIHI", "Name", "Phone"];
                this.limitInput = [];

                let limitTable = document.createElement('table');
                limitTable.setAttribute('width', '100%');

                for (let ii = 0; ii < 6; ++ii)
                {
                    let limitSpan  = document.createElement('span');
                    limitSpan.setAttribute('class', 'alarmLimit-label-text');
                    limitSpan.innerHTML = limitName[ii];

                    let limitSpanTd = document.createElement('td');
                    limitSpanTd.setAttribute('width', '50%');
                    limitSpanTd.setAttribute('align', 'left');
                    limitSpanTd.appendChild(limitSpan);

                    this.limitInput[ii] = document.createElement('input');
                    this.limitInput[ii].setAttribute('class', 'alarmLimit-input-text');
                    this.limitInput[ii].setAttribute('type', 'text');
                    this.limitInput[ii].setAttribute('value', '0');

                    let limitInputTd = document.createElement('td');
                    limitInputTd.setAttribute('width', '50%');
                    limitInputTd.setAttribute('align', 'right');
                    limitInputTd.appendChild(this.limitInput[ii]);

                    let limitTr = document.createElement('tr');
                    limitTr.appendChild(limitSpanTd);
                    limitTr.appendChild(limitInputTd);

                    limitTable.appendChild(limitTr);
                }
                let holdingTable = document.createElement('table');
                holdingTable.setAttribute('width', '100%');
                let holdingTableTr1 = document.createElement('tr');
                let holdingTableTr2 = document.createElement('tr');
                let holdingTableTd1 = document.createElement('td');
                let holdingTableTd2 = document.createElement('td');
                holdingTableTr1.appendChild(holdingTableTd1);
                holdingTableTr2.appendChild(holdingTableTd2);
                holdingTable.appendChild(holdingTableTr1);
                holdingTable.appendChild(holdingTableTr2);
                holdingTableTd1.appendChild(limitTable);

                let buttonTable = document.createElement('table');
                buttonTable.setAttribute('width', '100%');
                let buttonTableTr1 = document.createElement('tr');
                let buttonTableTd1 = document.createElement('td');
                let buttonTableTd2 = document.createElement('td');
                buttonTableTd1.setAttribute('width', '50%');
                buttonTableTd1.setAttribute('align', 'center');
                buttonTableTd1.setAttribute('style', 'padding-top:20px;');
                buttonTableTd2.setAttribute('width', '50%');
                buttonTableTd2.setAttribute('style', 'padding-top:20px;');
                buttonTableTd2.setAttribute('align', 'center');
                buttonTableTr1.appendChild(buttonTableTd1);
                buttonTableTr1.appendChild(buttonTableTd2);
                buttonTable.appendChild(buttonTableTr1);
                holdingTable.appendChild(buttonTable);

                this.setButton = document.createElement('button');
                this.setButton.setAttribute('class', 'btn alarmLimit-button');
                this.setButton.innerHTML = 'Set';
                this.setButton.onclick = event => {this.changeLimits()};
                buttonTableTd1.appendChild(this.setButton);

                this.cancelButton = document.createElement('button');
                this.cancelButton.setAttribute('class', 'btn alarmLimit-button');
                this.cancelButton.innerHTML = 'Cancel';
                this.cancelButton.onclick = event => {this.cancel()};
                buttonTableTd2.appendChild(this.cancelButton);

                let limitCardBody = document.createElement('div');
                limitCardBody.setAttribute('class', 'alarmLimit-card-body');
                limitCardBody.appendChild(limitTable);

                this.cardTitle = document.createElement('p');
                this.cardTitle.setAttribute('class', 'alarmLimit-card-title');
                this.cardTitle.innerHTML = "Cube";

                this.limitCard = document.createElement('div');
                this.limitCard.setAttribute('class', 'dialog-modal-content card alarmLimit-card');
                this.limitCard.appendChild(this.cardTitle);
                this.limitCard.appendChild(limitCardBody);
                this.limitCard.appendChild(holdingTable);

                this.cardContainer.appendChild(this.limitCard);
            }
            showAlarmConfig(show)
            {
                if (show)
                {
                    this.cardContainer.style.display = "block";
                    this.limitCard.style.display = "block";
                }
                else
                {
                    this.cardContainer.style.display = "none";
                }
            }
            cancel()
            {
                this.cardContainer.style.display = "none";
            }
            changeLimits()
            {
                for (let ii = 0; ii < 4; ++ii) 
                {
                    if (isNaN(this.limitInput[ii].value) )
                    {
                        this.cardContainer.style.display = "none";
                        return;
                    }
                }
                this.tray[this.cubeName].alarm.limits.lolo = Number(this.limitInput[0].value);
                this.tray[this.cubeName].alarm.limits.low  = Number(this.limitInput[1].value);
                this.tray[this.cubeName].alarm.limits.high = Number(this.limitInput[2].value);
                this.tray[this.cubeName].alarm.limits.hihi = Number(this.limitInput[3].value);
                this.tray[this.cubeName].alarm.notify.name = this.limitInput[4].value;
                this.tray[this.cubeName].alarm.notify.number = this.limitInput[5].value;
                let configInfo = 
                {
                    cube : this.cubeName,
                    limits: this.tray[this.cubeName].alarm.limits,
                    notify: this.tray[this.cubeName].alarm.notify
                };
                this.updateTrayConfig(configInfo);
                this.cardContainer.style.display = "none";
            }
            editAlarmConfig(cubeName, tray)
            {
                this.cubeName = cubeName;
                this.tray = tray;
                this.cardTitle.innerHTML = this.tray.type + "." + this.tray.name + "." + this.cubeName;
                this.limitInput[0].value = this.tray[this.cubeName].alarm.limits.lolo.toString();
