function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numTrays = objectLength(msg.payload);
var timeStampList = [];

for (var itray = 0; itray < numTrays; ++itray)
{
