    payload         : [queryFilter, projectionFilter],
    trayList        : msg.payload.trayList
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? 1 : ((x > y) ? -1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic : 'getSettingsLog',
        payload:{
            topic           : 'getSettingsLog',
            payload         : null,
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) 
{
    let splitTopic = msg.payload[ii].topic.split('/');
    let type = splitTopic[1];
    let name = splitTopic[2];
    let allowedTray = false;
    for (let itray in msg.trayList)
    {
        if ((msg.trayList[itray].type == type) && (msg.trayList[itray].name == name) )
        {
            allowedTray = true;
            break;
        }
    }
    if (allowedTray)
    {
        let data = 
        {
            type        : type,
            name        : name,
            cube        : msg.payload[ii].tray.cube,
            value       : msg.payload[ii].tray.value,
            timeStamp   : msg.payload[ii].timeStamp,
            username    : msg.payload[ii].username,
            url         : msg.payload[ii].ipInfo.url
        }
        records.push(data); 
    }
}
sortByKey(records, 'timeStamp');

return {
    payload:{
        topic           : 'getSettingsLog',
        payload         : records,
        userID          : msg.userID,
    }
};

msg.userData = {cred : {}, url : msg.payload.url};
msg.userData.cred = 
{
