    optCode : msg.payload.optCode,
};
msg.payload =  [{username : msg.userData.cred.username },  {projection  :{_id :   0}}]
return msg;

function isEmpty(obj) {
    for(var key in obj) {
        if(obj.hasOwnProperty(key))
            return false;
    }
    return true;
}
if (isEmpty(msg.payload))
{
    delete msg.userData['cred'];
    msg.payload.errorMsg = 'Error: Invalid Credentials';
    return [msg, null,null,null];
}
let { authenticator } = global.get('otplib');
if (("secret2Fa" in msg.payload))
{
    if (!authenticator.check(msg.userData.cred.optCode, msg.payload.secret2Fa))
    {
        delete msg.userData['cred'];
        msg.payload = {errorMsg: 'Error: Invalid Credentials'};
        return [msg, null,null,null];
    }
    else
    {
        msg.payload = JSON.parse(JSON.stringify(msg.payload.profile));
        msg.payload['username'] = msg.userData.cred.username;
        msg.payload['expDate'] = -1;
        if ( msg.payload.timeoutMin > 0)
        {
            msg.payload['expDate'] = new Date().getTime() + msg.payload.timeoutMin * 60000;
        }
        delete msg.userData.cred;
        return [null,msg,null,null];
    }
}
if (("tempSecret2Fa" in msg.payload))
{
    if (!authenticator.check(msg.userData.cred.optCode, msg.payload.tempSecret2Fa))
    {
        delete msg.userData['cred'];
        msg.payload = {errorMsg: 'Error: Invalid Credentials'};
        return [msg, null,null,null];
    }
    else
    {
        let queryFilter = 
        {
            username : msg.userData.cred.username
        }
        let updateSecretMsg = {topic:'updateSecret', payload:[queryFilter, { $set: {secret2Fa:msg.payload.tempSecret2Fa} }]};
        msg.payload = JSON.parse(JSON.stringify(msg.payload.profile));
        msg.payload['username'] = msg.userData.cred.username;
        msg.payload['expDate'] = -1;
        if ( msg.payload.timeoutMin > 0)
        {
            msg.payload['expDate'] = new Date().getTime() + msg.payload.timeoutMin * 60000;
        }
        delete msg.userData.cred;

        return [null,msg,updateSecretMsg,null];
    }
}
msg.payload = {username: msg.userData.cred.username};
delete msg.userData['cred'];
return [null,null,null,msg];

