for (let istyle = 0; istyle < msg.payload.sheets.length; ++istyle)
{
    for (let icss = 0; icss < msg.payload.sheets[istyle].css.length; ++icss)
    {
        cssString = cssString + jsonToCss(msg.payload.sheets[istyle].css[icss]);
    }
}
msg.config['css'] = cssString;
