//cubeRowParameterView
        class CubeRowParameterView 
        {
            constructor(card, config)
            {
                this.card = card;
                this.config = JSON.parse(JSON.stringify(config));
                this.tray = {type:this.config.type, name:this.config.name};
                this.tray[this.config.cubeName] = this.config.cube;
            }

            onDocumentReady(parentElement)
            {
                let inLineTable = document.createElement('table');
                inLineTable.setAttribute('width', '100%');
                let inLineRow  = document.createElement('tr');
                let labelColumn  = document.createElement('td');
                labelColumn.setAttribute('class', 'cubeRowParameterView-label-column');

                let labelSpan  = document.createElement('span');
                labelSpan.innerHTML = this.config.type + "." + this.config.name + "." + this.config.cubeName;
                labelColumn.appendChild(labelSpan);
                inLineRow.appendChild(labelColumn);

                let widgetColumns = [];
                for (let iw = 0; iw < 3; ++iw)
                {
                    let iwcol  = document.createElement('td');
                    iwcol.setAttribute('class', 'cubeRowParameterView-widget-column-' + (iw + 1).toString());
                    inLineRow.appendChild(iwcol);
                    widgetColumns.push(iwcol);
                }

                this.addComponents(widgetColumns);

                let alarmColumn  = document.createElement('td');
                alarmColumn.setAttribute('class', 'cubeRowParameterView-alarmConfig-column');
                this.alarmButton = document.createElement('input');
                this.alarmButton.setAttribute('type', 'image');
                this.alarmButton.setAttribute('src', '/img/greenLight.png');
                this.alarmButton.setAttribute('class', 'cubeRowParameterView-alarmConfig-button');
                this.alarmButton.onclick = event => {this.changeAlarmConfig()};
                alarmColumn.appendChild(this.alarmButton);
                inLineRow.appendChild(alarmColumn);

                inLineTable.appendChild(inLineRow);
                parentElement.appendChild(inLineTable);
                this.trayUpdate();
            }

            trayUpdate()
            {
                this.updateCubeValue();
                switch(this.tray[this.config.cubeName].alarm.value)
                {
                    case 0:
                        this.alarmButton.src="/img/greenLight.png";
                        break;
                    case 1:
                        this.alarmButton.src="/img/blueLight.png";
                        break;
                    case 2:
                        this.alarmButton.src="/img/yellowLight.png";
                        break;
                    case 3:
                        this.alarmButton.src="/img/purpleLight.png";
                        break;
                    case 4:
                        this.alarmButton.src="/img/redLight.png";
                        break;
                    default:
                        this.alarmButton.src="/img/greyLight.png";
                        break;

                }
            }
            changeAlarmConfig()
            {
                this.card.app.alarmLimitDialog.editAlarmConfig(this.config.cubeName, this.tray);
            }

        }

//numberSetCube
        class NumberSetCube   extends CubeRowParameterView
        {
            addComponents(widgetColumns)
            {
                this.inputBox = document.createElement('input');
                this.inputBox.setAttribute('class', 'numberSetCube-inputbox');
                this.inputBox.setAttribute('type', 'text');
                this.inputBox.setAttribute('value', '0');
                this.inputBox.oninput = event => {this.inputChange()};
                widgetColumns[0].appendChild(this.inputBox);

                this.unitSpan  = document.createElement('span');
                this.unitSpan.setAttribute('class', 'cubeRowParameterView-unit-span');
                this.unitSpan.innerHTML = '';
                widgetColumns[1].appendChild(this.unitSpan);

                this.settingButton = document.createElement('button');
                this.settingButton.setAttribute('class', 'btn btn-block numberSetCube-button');
                this.settingButton.innerHTML = '&#10003;';
                this.settingButton.onclick = event => {this.changeSetting()};
                widgetColumns[2].appendChild(this.settingButton);

                this.showSettingButton(false);
            }

            showSettingButton(showButton)
            {
                if (showButton)
                {
                    this.settingButton.style.display='block';
                }
                else
                {
                    this.settingButton.style.display='none';
                }
            }
            inputChange()
            {
                this.card.settingInProgress = true
                this.changed = true;
                this.showSettingButton(true);
            }
            changeSetting()
            {
                this.showSettingButton(false);
                if (isNaN(this.inputBox.value))
                {
                    this.changed = false;
                    return;
                }
                let payload = {'cube': this.config.cubeName, 'value':Number(this.inputBox.value)};
                let actionMsg = 
                    {
                        topic   : this.card.app.box + '/' +  this.tray.type + '/' +  this.tray.name + '/setting/setting',
                        payload : payload
                    };
                this.card.app.sendActionMsg('sendSettingToMqtt','setting',actionMsg);
                this.changed = false;
                this.card.settingInProgress = false;
            }
            updateCubeValue()
            {
                if (!this.changed) 
                {
                    this.inputBox.value = this.tray[this.config.cubeName].value.toString();
                    this.unitSpan.innerHTML    = this.tray[this.config.cubeName].unit;
                }

            }
        }

