//cubeRowCard
        class CubeRowCard 
        {
            constructor(app, config)
            {
                this.app = app;
                this.config = config;
                this.cubeRows = [];
                this.settingInProgress = false;
            }
            onDocumentReady()
            {
                let containerRowDiv = document.createElement('div');
                containerRowDiv.setAttribute('class', 'row cubeRowCard-containerRow');
                document.getElementById('appContent').appendChild(containerRowDiv);

                let containerColDiv = document.createElement('div');
                containerColDiv.setAttribute('class', 'col-md-12');
                containerColDiv.setAttribute('align', 'center');
                containerRowDiv.appendChild(containerColDiv);

                let cardDiv = document.createElement('div');
                cardDiv.setAttribute('class', 'card cubeRowCard');
                containerColDiv.appendChild(cardDiv);

                let titleTable = document.createElement('table');
                titleTable.setAttribute('width', '100%');
                let titleRow  = document.createElement('tr');
                let titleCol  = document.createElement('td');
                titleCol.setAttribute('width', '85%');
                titleCol.setAttribute('align', 'left');
                let titleSpan  = document.createElement('span');
                titleSpan.setAttribute('class', 'cubeRowCard-title');
                titleSpan.innerHTML = this.config.title;
                titleCol.appendChild(titleSpan);
                titleRow.appendChild(titleCol);
                let buttonCol  = document.createElement('td');
                buttonCol.setAttribute('width', '15%');

                this.displayButton = document.createElement('button');
                this.displayButton.setAttribute('class', 'btn btn-block cubeRowCard-title-button');
                this.displayButton.innerHTML = '-';
                this.displayButton.onclick = event => {this.toggle()};
                buttonCol.appendChild(this.displayButton);
                titleRow.appendChild(buttonCol);

                this.cardBodyDiv = document.createElement('div');
                this.cardBodyDiv.setAttribute('class', 'cubeRowCard-body');
                this.cardBodyDiv.setAttribute('align', 'center');
                this.cardBodyTable = document.createElement('table');
                this.cardBodyTable.setAttribute('width', '100%');
                this.cardBodyDiv.appendChild(this.cardBodyTable);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);
                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }

            }
            toggle()
            {
                if (this.cardBodyDiv.style.display === "none") 
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }

            }
            getAlarmList()
            {
                if (this.settingInProgress) return;
                let trayList = [];
                for (let itrayType in this.app.trays)
                {
                    for (let itrayName in this.app.trays[itrayType])
                    {
                        trayList.push(this.app.trays[itrayType][itrayName].type + '/' + this.app.trays[itrayType][itrayName].name + '/');
                    }
                }
                let actionMsg = 
                {
                    trayList : trayList
                };
                this.app.sendActionMsg('getAlarmList', 'readDatabase', actionMsg);
            }
            processAlarmList(alarmList)
            {
                destroyNode(this.cardBodyTable);
                this.cardBodyTable = document.createElement('table');
                this.cardBodyTable.setAttribute('width', '100%');
                this.cardBodyDiv.appendChild(this.cardBodyTable);
                for (let icube in alarmList)
                {
                    let cubeRow = null; 
                    if (alarmList[icube].cube.action == 'reading')
                    {
                        cubeRow = new NumberReadCube(this,alarmList[icube]);
                    }
                    if (alarmList[icube].cube.action == 'setting')
                    {
                        cubeRow = new NumberSetCube(this,alarmList[icube]);
                    }

                    let cubeRowRow =  document.createElement('tr');
                    let cubeRowCol  = document.createElement('td');
                    cubeRowCol.setAttribute('width', '100%');
                    cubeRow.onDocumentReady(cubeRowCol);
                    cubeRowRow.appendChild(cubeRowCol);
                    this.cardBodyTable.appendChild(cubeRowRow);
                    this.cubeRows.push(cubeRow);

                }
            }            

        }

