return msg;
function getRandomInt(max)
{
  return Math.floor(Math.random() * Math.floor(max));
}

let trayList = []
let viewAllTrays = false;
for (let allowedTray of msg.token.trays) 
{
    if (allowedTray.type == '*') 
    {
        viewAllTrays = true;
        break;
    }
}
for( let ii in msg.payload ) 
{
    if( msg.payload.hasOwnProperty(ii) ) 
    {
        let trayDb = msg.payload[ii];
        if (viewAllTrays)
        {
            trayList.push(JSON.parse(JSON.stringify(trayDb)));
        }
        else
        {
            for (let allowedTray of msg.token.trays) 
            {
                if (allowedTray.type == trayDb.type) 
                {
                    if (allowedTray.name == '*')
                    {
                        trayList.push(JSON.parse(JSON.stringify(trayDb)));
                    }
                    else
                    {
                        if (allowedTray.name == trayDb.name)
                        {
                            trayList.push(JSON.parse(JSON.stringify(trayDb)));
                        }
                    }
                }
            }
        }
    }
}

let trayListType = []
let trayListSort = [];
for( let ii in trayList ) 
{
    let index = trayListType.findIndex(tray => tray.type == trayList[ii].type);
    if (index < 0)
    {
        trayListType.push({type:trayList[ii].type});
        trayListSort.push([trayList[ii]]);
    }
    else
    {
        trayListSort[index].push(trayList[ii]);
    }
}
trayListSort.sort( 
    function(a, b) 
    {
        var x = a[0]['type'].toUpperCase(); 
        var y = b[0]['type'].toUpperCase();
        return ((x < y) ? -1 : ((x > y) ? 1 : 0));
    }
);
for( let ii in trayListSort ) 
{
    trayListSort[ii].sort( 
        function(a, b) 
        {
            var x = a['name'].toUpperCase(); 
            var y = b['name'].toUpperCase();
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        }
    );
}

let app = msg.req['_parsedUrl'].pathname.split('/')[1];
msg['urlInput'] = JSON.stringify( {box:global.get('box'), trayList:trayListSort});
msg.payload = [{ app: app},{projection  :{_id : 0} }]

return msg;

let cssObj = JSON.stringify(msg.payload);
let cssString = "";
for (let istyle = 0; istyle < msg.payload.sheets.length; ++istyle)
{
    for (let icss = 0; icss < msg.payload.sheets[istyle].css.length; ++icss)
    {
        cssString = cssString + jsonToCss(msg.payload.sheets[istyle].css[icss]);
    }
}
msg.config['css'] = cssString;
msg.payload = JSON.parse(JSON.stringify(msg.config));
delete msg.config;
return msg;

// From http://www.featureblend.com/css-json.html
function jsonToCss(cssjson)
{
    var styleStr = "\t\t";
    for(var i in cssjson)
    {
        styleStr += i + "\n\t\t{\n"
        for(var j in cssjson[i]){
            styleStr += "\t\t\t" + j + "\t\t:\t" + cssjson[i][j] + ";\n"     
        }
        styleStr += "\t\t}\n"  
    }
    return styleStr;
}
function getRandomInt(max)
{
  return Math.floor(Math.random() * Math.floor(max));
}

