                this.cardBodyDiv.appendChild(addRowButtonDiv);
                addRowButtonDiv.style.paddingTop = "10px";
                addRowButtonDiv.style.paddingBottom = "10px";

                let addRowButtonTable = document.createElement('table');
                addRowButtonDiv.appendChild(addRowButtonTable);
                addRowButtonTable.setAttribute('width', '100%');
                let addRowButtonTableR1 = document.createElement('tr');
                addRowButtonTable.appendChild(addRowButtonTableR1);
                let addRowButtonTableR1C1 = document.createElement('td');
                addRowButtonTableR1.appendChild(addRowButtonTableR1C1);
                addRowButtonTableR1C1.setAttribute('width', '33%');
                let addRowButtonTableR1C2 = document.createElement('td');
                addRowButtonTableR1.appendChild(addRowButtonTableR1C2);
                addRowButtonTableR1C2.setAttribute('width', '33%');
                let addRowButtonTableR1C3 = document.createElement('td');
                addRowButtonTableR1.appendChild(addRowButtonTableR1C3);
                addRowButtonTableR1C3.setAttribute('width', '33%');

                this.addRowButton = document.createElement('button');
                this.addRowButton.setAttribute('class', 'btn-primary btn-block cubeListChooser-button');
                this.addRowButton.innerHTML = 'Add row';
                this.addRowButton.onclick = event => {this.addTrace()};
                addRowButtonTableR1C1.appendChild(this.addRowButton);

                this.saveConfigButton = document.createElement('button');
                this.saveConfigButton.setAttribute('class', 'btn-warning btn-block cubeListChooser-button');
                this.saveConfigButton.innerHTML = 'Save Config';
                this.saveConfigButton.onclick = event => {this.saveConfig()};
                addRowButtonTableR1C3.appendChild(this.saveConfigButton);

                titleTable.appendChild(titleRow);
                cardDiv.appendChild(titleTable);
                cardDiv.appendChild(this.cardBodyDiv);
                if (this.config.displayOnOpen)
                {
                    this.cardBodyDiv.style.display = "block";
                    this.displayButton.innerHTML = '-';
                }
                else
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }
