                    });
            }
            selectAxis(itrace,axis)
            {
                let newTraceAxis = this.cubeData[itrace].traceAxis;
                if (axis == 1)
                {
                    switch(this.cubeData[itrace].traceAxis)
                    {
                        case 0:
                            newTraceAxis = 1;
                            break;
                        case 1:
                            newTraceAxis = 0;
                            break;
                        case 2:
                            newTraceAxis = 1;
                            break;
                        default:
                            break;
                    }
                }
                else
                {
                    switch(this.cubeData[itrace].traceAxis)
                    {
                        case 0:
                            newTraceAxis = 2;
                            break;
                        case 1:
                            newTraceAxis = 2;
                            break;
                        case 2:
                            newTraceAxis = 0;
                            break;
                        default:
                            break;
                    }
                }
                this.cubeData[itrace].traceAxis = newTraceAxis;
                switch(this.cubeData[itrace].traceAxis)
                {
                    case 0:
                        this.y1AxisElement[itrace].checked = false;
                        this.y2AxisElement[itrace].checked = false;
                        break;
                    case 1:
                        this.y1AxisElement[itrace].checked = true;
                        this.y2AxisElement[itrace].checked = false;
                        break;
                    case 2:
                        this.y1AxisElement[itrace].checked = false;
                        this.y2AxisElement[itrace].checked = true;
                        break;
                    default:
                        this.y1AxisElement[itrace].checked = false;
                        this.y2AxisElement[itrace].checked = false;
                        break;
                }
            }
            selectPts(itrace)
            {
                this.cubeData[itrace].tracePts = !this.cubeData[itrace].tracePts;
                this.ptsElement[itrace].checked = this.cubeData[itrace].tracePts;
            }
            addTrace()
            {
                this.addTraceRow();
            }
            changeAlarmConfig(itrace)
            {
                if (!this.cubeData[itrace].validCube) return;
                this.app.alarmLimitDialog.editAlarmConfig(this.cubeData[itrace].cube, this.cubeData[itrace].tray);
            }
            inputChange(itrace)
            {
                this.cubeData[itrace].settingInProgress = true;
                this.cubeSettingButtonElement[itrace].style.visibility = "visible";
            }
            changeSetting(itrace)
            {

                this.cubeData[itrace].settingInProgress = false;
                this.cubeSettingButtonElement[itrace].style.visibility = "hidden";
                console.log('change setting')
                if (isNaN(this.cubeValueElement[itrace].value))
                {
                    return;
                }
                let cubeValue = Number(this.cubeValueElement[itrace].value);
                let  actionMsg = 
                {
                    topic   : this.app.box + '/' + this.cubeData[itrace].type + '/' + this.cubeData[itrace].name + '/setting/setting',
                    payload : {'cube': this.cubeData[itrace].cube, 'value':cubeValue}
                };
                this.app.sendActionMsg('sendSettingToMqtt','setting',actionMsg);
            }
            disableCubeList(disable)
            {
                for (let itrace in this.traySelect)
                {
                    this.traySelect[itrace].disabled = disable;
                    this.nameSelect[itrace].disabled = disable;
                    this.propSelect[itrace].disabled = disable;
                    this.cubeSelect[itrace].disabled = disable;
                    this.y1AxisElement[itrace].disabled = disable;
                    this.y2AxisElement[itrace].disabled = disable;
                    this.ptsElement[itrace].disabled = disable;
                }
                this.addRowButton.disabled = disable;
            }
            timePlotRunning(isTimePlotRunning)
            {
                this.isTimePlotRunning = isTimePlotRunning;
                this.disableCubeList(isTimePlotRunning);
                if (isTimePlotRunning)
                {
                    this.disableCubeList(true);
                }
                else
                {
                    if (!this.isArchivePlotRunning) this.disableCubeList(false);
                }
           }
            archivePlotRunning(isArchivePlotRunning)
            {
                this.isArchivePlotRunning = isArchivePlotRunning;
                if (isArchivePlotRunning)
                {
                    this.disableCubeList(true);
                }
                else
                {
                    if (!this.isTimePlotRunning) this.disableCubeList(false);
                }
            }
            initArrays()
            {
                this.cubeData = [];
                this.traySelect = [];
                this.nameSelect = [];
                this.propSelect = [];
                this.cubeSelect = [];
                this.cubeValueElement = [];
                this.cubeUnitElement = [];
                this.cubeSettingButtonElement = [];        
                this.y1AxisElement = [];  
                this.y2AxisElement = [];  
                this.ptsElement = [];  
                this.alarmButton = [];
            }
            saveConfig()
            {
                this.app.inputDialog.showDialog('Enter title:',(okchoice) => 
                {
                    if (okchoice)
                    {
                        let title = this.app.inputDialog.inputBox.value.replace(/[^a-zA-Z0-9 ]/g, '');
                        this.app.cubeExplorerConfigCard.saveConfig(title);
                    }
                });
            }
        }

