if (msg.token.timeoutMin > 0) msg.token.expDate = new Date().getTime() + msg.token.timeoutMin * 60000;
delete msg.token['iat']
msg.payload = JSON.parse(JSON.stringify(msg.token));
delete msg['token'];
return msg;

