                cardBodyTable.appendChild(cardBodyR1);
                let cardBodyR1C1 = document.createElement('td');
                cardBodyR1C1.setAttribute('width', '100%');
                cardBodyR1.appendChild(cardBodyR1C1);
                let controlButtonTable = document.createElement('table');
                controlButtonTable.setAttribute('width', '100%');
                cardBodyR1C1.appendChild(controlButtonTable);
                let controlButtonTableR1 = document.createElement('tr');
                controlButtonTable.appendChild(controlButtonTableR1);
                let controlButtonTableR1C1 = document.createElement('td');
                controlButtonTableR1.appendChild(controlButtonTableR1C1);
                controlButtonTableR1C1.style.width = "20%"
                let controlButtonTableR1C2 = document.createElement('td');
