                    {
                        this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                        Plotly.newPlot('timePlotChart', this.timePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                    }
                } 
                else 
                {
                    this.cardBodyDiv.style.display = "none";
                    this.displayButton.innerHTML = '+';
                }

            }
            resize()
            {
                if ((this.cardBodyDiv.style.display != "none") && (this.timePlotTraces.length > 0) )
                {
                    this.config.plotlyLayout.width =  (this.cardBodyDiv.offsetWidth - 40).toString();
                    Plotly.newPlot('timePlotChart', this.timePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                }
            }
            setupPlot()
            {
                this.timePlotTraces = [];
                for (let icube in this.cubeData)
                {
                    let axis = 'y1';
                    if (this.cubeData[icube].traceAxis == 2) axis = 'y2';
                    let modeType = 'lines';
                    if (this.cubeData[icube].tracePts) modeType = 'markers';
                    let trace = 
                    {
                        x: [],
                        y: [],
                        marker: {size: this.config.markerSize},
                        name: axis + ' ' + this.cubeData[icube].type + '.' + this.cubeData[icube].name + '.' + this.cubeData[icube].cube  + ' (' + this.cubeData[icube].tray[this.cubeData[icube].cube].unit + ')',
                        yaxis: axis,
                        type: 'scatter',
                        mode: modeType,
                    };
                    this.timePlotTraces.push(trace);
                }
            }
            addTracePointsToTimePlot()
            {
                if (!this.plotRunning) return;
                let now = new Date().getTime();
                let plotBeginning = now - this.config.timePlotLength;
                let removeDone = false;
                if (this.timePlotTraces.length < 1) return;
                if (this.timePlotTraces[0].x.length < 1) removeDone = true;
                while(!removeDone)
                {
                    if (this.timePlotTraces[0].x[0] < plotBeginning)
                    {
                        for (let ii = 0; ii < this.timePlotTraces.length; ++ii)
                        {
                            this.timePlotTraces[ii].x.shift();
                            this.timePlotTraces[ii].y.shift();
                        }
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                for (let icube in this.cubeData)
                {
                    this.timePlotTraces[icube].x.push(now);
                    this.timePlotTraces[icube].y.push(this.cubeData[icube].tray[this.cubeData[icube].cube].value);
                }
                Plotly.newPlot('timePlotChart', this.timePlotTraces, this.config.plotlyLayout, this.config.plotlyDisplay);
                this.resize();
            }
            startStopPlot()
            {
                if (this.plotRunning)
                {
                    this.plotRunning = false;
                    this.cubeListChooser.timePlotRunning(false);
                    this.startStopButton.innerHTML = 'Start';
                    this.startStopButton.classList.replace('btn-warning', 'btn-primary');
                }
                else
                {
                    let validTrace = false;
                    this.cubeData = [];
                    for (let itrace in this.cubeListChooser.cubeData)
                    {
                        if (this.cubeListChooser.cubeData[itrace].validCube)
                        {
                            if (this.cubeListChooser.cubeData[itrace].traceAxis > 0)
                            {
                                validTrace = true;
                                this.cubeData.push(this.cubeListChooser.cubeData[itrace]);
                            }
                        }
                    }
                    if (validTrace)
                    {
                        this.plotRunning = true;
                        this.cubeListChooser.timePlotRunning(true);
                        this.startStopButton.innerHTML = 'Stop';
                        this.startStopButton.classList.replace('btn-primary', 'btn-warning');
                        this.setupPlot();
                    }
                }
            }
        }

