                headRow.appendChild(this.createHeaderLabel("10%", "", "center"));
                this.cardBodyTable.appendChild(headRow);

                this.cardBodyDiv.appendChild(this.cardBodyTable);

                for (let isetup in this.userConfigs)
                {
                    let setupRow = document.createElement('tr');
                    this.cardBodyTable.appendChild(setupRow);

                    let titleCol = document.createElement('td');
                    setupRow.appendChild(titleCol);
                    let titleSpan = document.createElement('span');
                    titleCol.appendChild(titleSpan);
                    titleSpan.setAttribute('class','cubeListChooser-tableText3');
                    titleSpan.innerHTML = this.userConfigs[isetup].title;

                    let loadButtonCol = document.createElement('td');
                    setupRow.appendChild(loadButtonCol);
                    loadButtonCol.setAttribute('align','center');
                    loadButtonCol.style.padding = "10px";
                    let loadButton = document.createElement('button');
                    loadButtonCol.appendChild(loadButton);
                    loadButton.setAttribute('class','btn-primary btn-block cubeListChooser-button');
                    loadButton.innerHTML = "Load";
                    loadButton.onclick = event => {this.loadSetting(isetup)};

                    let deleteButtonCol = document.createElement('td');
                    setupRow.appendChild(deleteButtonCol);
                    deleteButtonCol.setAttribute('align','center');
                    deleteButtonCol.style.padding = "10px";
                    let deleteButton = document.createElement('button');
                    deleteButtonCol.appendChild(deleteButton);
                    deleteButton.setAttribute('class','btn-danger btn-block cubeListChooser-button');
                    deleteButton.innerHTML = "Delete";
                    deleteButton.onclick = event => {this.deleteSetting(isetup)};
                }
            }
            loadSetting(isetup)
            {
                if (this.cubeListChooser.isTimePlotRunning)
                {
                    this.app.acknowledgeDialog.showDialog('Error: Time Plot Active',function(){});
                    return;
                }
                if (this.cubeListChooser.isArchivePlotRunning)
                {
                    this.app.acknowledgeDialog.showDialog('Error: Archive fetch in progress',function(){});
                    return;
                }
                this.app.inputDialog.inputBox.value = this.userConfigs[isetup].title;
                let tableParent = this.cubeListChooser.cardBodyTable.parentNode;
                destroyNode(this.cubeListChooser.cardBodyTable);
                this.cubeListChooser.cardBodyTable = document.createElement('table');
                this.cubeListChooser.cardBodyTable.setAttribute('width', '100%');
                tableParent.insertBefore(this.cubeListChooser.cardBodyTable, tableParent.firstChild);
                this.cubeListChooser.createHeaderRow();
                this.cubeListChooser.initArrays();

                let irow = 0;
                this.startLoadingRow(irow, isetup)
            }
            startLoadingRow(irow, isetup)
            {
                this.cubeListChooser.addTraceRow();
                let itraySelect = -1;
                let childElementCount  = this.cubeListChooser.traySelect[irow].childElementCount;
                for (let itype = 0; itype < childElementCount; ++itype)
                {
                    if (this.cubeListChooser.traySelect[irow][itype].innerHTML == this.userConfigs[isetup].configs[irow].type)
                    {
                        itraySelect = itype;
                        break;
                    }
                }
                if (itraySelect > 0)
                {
                    this.cubeListChooser.traySelect[irow].selectedIndex = itraySelect;
                    this.cubeListChooser.traySelected(irow);

                    let inameSelect = -1;
                    let childElementCount  = this.cubeListChooser.nameSelect[irow].childElementCount;
                    for (let iname = 0; iname < childElementCount; ++iname)
                    {
                        if (this.cubeListChooser.nameSelect[irow][iname].innerHTML == this.userConfigs[isetup].configs[irow].name)
                        {
                            inameSelect = iname;
                            break;
                        }
                    }
                    if (inameSelect > 0)
                    {
                        this.cubeListChooser.nameSelect[irow].selectedIndex = inameSelect;
                        this.cubeListChooser.nameSelected(irow);
                        let ipropSelect = -1;
                        let childElementCount  = this.cubeListChooser.propSelect[irow].childElementCount;
                        for (let iprop = 0; iprop < childElementCount; ++iprop)
                        {
                            if (this.cubeListChooser.propSelect[irow][iprop].innerHTML == this.userConfigs[isetup].configs[irow].prop)
                            {
                                ipropSelect = iprop;
                                break;
                            }
                        }
                        if (ipropSelect > 0)
                        {
                            this.cubeListChooser.propSelect[irow].selectedIndex = ipropSelect;
                            this.cubeListChooser.propSelected(irow);

                            this.pollCubeSelectChildren(irow, isetup, 0, 50, 100);
                        }
                        else
                        {
                            console.log("Loading configuration failed");
                            alert("Loading configuration failed");
                        }
                    }
                    else
                    {
                        console.log("Loading configuration failed");
                        alert("Loading configuration failed");
                    }
                }
                else
                {
                    console.log("Loading configuration failed");
                    alert("Loading configuration failed");
                }

            }
            continueLoadingRow(irow, isetup)
            {
                let childElementCount  = this.cubeListChooser.cubeSelect[irow].childElementCount;

                let icubeSelect = -1;
                for (let icube = 0; icube < childElementCount; ++icube)
                {
                    if (this.cubeListChooser.cubeSelect[irow][icube].innerHTML == this.userConfigs[isetup].configs[irow].cube)
                    {
                        icubeSelect = icube;
                        break;
                    }
                }
                if (icubeSelect >= 0)
                {
                    this.cubeListChooser.cubeSelect[irow].selectedIndex = icubeSelect;
                    this.cubeListChooser.cubeSelected(irow);
                    this.cubeListChooser.cubeData[irow].traceAxis = this.userConfigs[isetup].configs[irow].axis;
                    switch(this.cubeListChooser.cubeData[irow].traceAxis)
                    {
                        case 0:
                            this.cubeListChooser.y1AxisElement[irow].checked = false;
                            this.cubeListChooser.y2AxisElement[irow].checked = false;
                            break;
                        case 1:
                            this.cubeListChooser.y1AxisElement[irow].checked = true;
                            this.cubeListChooser.y2AxisElement[irow].checked = false;
                            break;
                        case 2:
                            this.cubeListChooser.y1AxisElement[irow].checked = false;
                            this.cubeListChooser.y2AxisElement[irow].checked = true;
                            break;
                        default:
                            this.cubeListChooser.y1AxisElement[irow].checked = false;
                            this.cubeListChooser.y2AxisElement[irow].checked = false;
                            break;
                    }
                    this.cubeListChooser.cubeData[irow].tracePts = this.userConfigs[isetup].configs[irow].pts;
                    this.cubeListChooser.ptsElement[irow].checked = this.cubeListChooser.cubeData[irow].tracePts;
                    let inextRow = irow + 1;
                    if (inextRow < this.userConfigs[isetup].configs.length)
                    {
                        this.startLoadingRow(inextRow, isetup);
                    }

                }
                else
                {
                    console.log("Loading configuration failed");
                    alert("Loading configuration failed");
                }
            }
            pollCubeSelectChildren(irow, isetup, icount, timeOutmS, countMax)
            {
                let childElementCount  = this.cubeListChooser.cubeSelect[irow].childElementCount;
                ++icount;
                if ((childElementCount <= 1) && (icount < countMax))
                {
                    setTimeout(() => {this.pollCubeSelectChildren(irow, isetup, icount, timeOutmS, countMax)}, timeOutmS);
                }
                else
                {
                    if (childElementCount > 1)
                    {
                        this.continueLoadingRow(irow, isetup);
                    }
                    else
                    {
                        console.log("Loading configuration failed");
                        alert("Loading configuration failed");
                    }
                }
            }
            saveConfig(title)
            {

                let configs = [];
                for (let icube in this.cubeListChooser.cubeData)
                {
                    if (this.cubeListChooser.cubeData[icube].validCube)
                    {
                        let config = 
                        {
                            "type" : this.cubeListChooser.cubeData[icube].type,
                            "name" : this.cubeListChooser.cubeData[icube].name,
                            "prop" : this.cubeListChooser.cubeData[icube].prop,
                            "cube" : this.cubeListChooser.cubeData[icube].cube,
                            "axis" : this.cubeListChooser.cubeData[icube].traceAxis,
                            "pts" :  this.cubeListChooser.cubeData[icube].tracePts
                        }
                        configs.push(config);
                    }
                }
                if (configs.length < 1) return;
                let configFile = 
                {
                    "title" : title,
                    "configs" : configs
                }
                let iuserConfig = -1;
                for (let ii in this.userConfigs)
                {
                    if (configFile.title == this.userConfigs[ii].title)
                    {
                        iuserConfig = ii;
                        break;
                    }
                }
                if (iuserConfig >= 0) 
                {
                    this.userConfigs[iuserConfig] = configFile;
                }
                else
                {
                    this.userConfigs.push(configFile);
                }
                let  actionMsg = 
                {
                    topic: 'addUserConfig',
                    payload: this.userConfigs
                };
                this.app.sendActionMsg('addUserConfig','readDatabase',actionMsg);


            }
            deleteSetting(isetup)
            {
                this.userConfigs.splice(isetup, 1)
                let  actionMsg = 
                {
                    topic: 'addUserConfig',
                    payload: this.userConfigs
                };
                this.app.sendActionMsg('addUserConfig','readDatabase',actionMsg);
            }
        }

