{
    $("#freezeButton").text('Running');
    $("#plotslices").val(timeMax.toString());
    dataArray = [];
    itimeSlice = 0;
    plotStarted = false;
    sliceViewActive = true;
    birdsEyeViewActive = false;
    topViewActive = false;
}
function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getTrayList':
                loadTrayList(msg.payload);
                break;
            case 'getNameList':
                loadNameList(msg);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getTrayValues':
