        switch(msg.topic)
        {
            case 'getTrayList':
                loadTrayList(msg.payload);
                break;
            case 'getNameList':
                loadNameList(msg);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getArchiveValues':
                archiveList.push(msg.payload);
                if (archiveRequestList.length > 0)
                {
                    getArchiveValues();
                }
                else
                {
                    plotArchive();
                }
                break;
            case 'permissionError':
                acknowledgeDialog('Permission', 'Error', msg.payload);
                break;
            case 'loginExpired':
                location.reload();
                break;
            default:
            // code block
        }
    }
}
