};

var projectionFilter =  {projection:{id:0}};
var newMsg = 
{
    topic           : 'getAttrList',
    userID          : msg.userID,
    payload         : [{$and : [{deviceType:msg.payload.deviceType}, {name:msg.payload.name}]}, projectionFilter],
    prop            : msg.payload.prop,
    name            : msg.payload.name,
    deviceType      : msg.payload.deviceType,
    trace           : msg.payload.trace
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numDevices = objectLength(msg.payload);
if (numDevices < 1) return null;
var device = msg.payload[0];
var attrList = [];
var keys = Object.keys(device);
for (var ii = 0; ii < keys.length; ++ii)
{
    if (device[keys[ii]].hasOwnProperty('type'))
    {
        if (device[keys[ii]].type == 'scalar')
        {
            if (device[keys[ii]].action == msg.prop)
            {
                attrList.push(keys[ii]);
            }
        }
    }
}
if (attrList.length < 1) return null;
return {
    topic:'getAttrList',
    payload:{
        topic           : 'getAttrList',
        userID          : msg.userID,
        prop            : msg.prop,
        name            : msg.name,
        deviceType      : msg.deviceType,
        trace           : msg.trace,
        attrList        : attrList

    }
};

