 var startDate = 0;
 var stopDate = 0;
 var csvFile = [];
 var archiveRequestList = [];
 var archiveList = [];

 function onDocumentReadySpecial()
 {
     for (var itrace = 0; itrace < numTraces; ++itrace)
     {
         $('#csv'+ itrace.toString()).removeAttr("href");
         $('#csv'+ itrace.toString()).css('color', 'black');
         $('#csv'+ itrace.toString()).css('text-decoration', 'none');
         $('#csv'+ itrace.toString()).css('font-weight', 'bold');
         csvFile[itrace] = null;

     }
     var now = new Date();
     console.log(now.toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', ''));
     var then = new Date(now.getTime() - 3600 * 24 * 1000);
     $( "#startDate" ).val(then.toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', ''));
     $( "#stopDate" ).val(now.toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', ''));
     $( function()
     {
         $('#startDate').datetimepicker();
     } );
     $( function()
     {
         $('#stopDate').datetimepicker();
     } );
     $('#timePlotCard').hide();
 }
 function onWebSocketMessageSpecial(msg)
