        {
            var cookies = document.cookie.split(';');
            var token = null;
            var cookieName = project + extension + "=";
            for (var icookie = 0; icookie < cookies.length; ++icookie)
            {
                var index = cookies[icookie].indexOf(cookieName);
                if (index >= 0)
                {
                    token = cookies[icookie].substring(index + cookieName.length);
                }
            }
            return token;
        }
        function sendActionMsg(topic,role,actionMsg)
        {
            var roleToken = "";
            if (remoteApp) roleToken  = getCookie('Role');
